// originally from https://gist.github.com/jiggzson/b5f489af9ad931e3d186
function scientificToDecimal(i){if(/\d+\.?\d*e[\+\-]*\d+/i.test(i)){var e=String(i).toLowerCase().split("e"),n=e.pop(),t=Math.abs(n),r=n/t,o=e[0].split(".");if(-1===r)i="0."+new Array(t).join("0")+o.join("");else{var a=o[1];a&&(t-=a.length),i=o.join("")+new Array(t+1).join("0")}}return i}
